/*
*******************************************************************************************
* 函 数 名: Value_Change_add
* 功能说明: 增加数值
* 形 参: 数值的地址
* 返 回 值: 无
*******************************************************************************************
*/
void Value_Change_add(void *p,u16 *time)
{
	
	s16 *q = (s16 *)p;
	(*time)++;
	if ((*time) < 10)
	{
		(*q)++;
	}
	else if ((*time) < 30)
	{
		(*q) += 10;
	}
	else if ((*time) >= 30)
	{
		(*q) += 100;
	}

}
/*
*******************************************************************************************
* 函 数 名: Value_Change_dec
* 功能说明: 减少数值
* 形 参: 数值的地址
* 返 回 值: 无
*******************************************************************************************
*/
void Value_Change_dec(void *p,u16 *time)
{
	
	s16 *q = (s16 *)p;
	(*time)++;
	if ((*time) < 10)
	{
		(*q)--;
	}
	else if ((*time) < 30)
	{
		(*q) -= 10;
	}
	else if ((*time) >= 30)
	{
		(*q) -= 100;
	}

}
